local SCRIPT_TITLE = 'Shift notes down V1.0'

--[[

Synthesizer V Studio Pro Script
 
lua file name: ShiftNotesDown.lua

This script will move pitch notes down
on selected notes.
If no notes selected piano-roll scroll down

Set shortcut to Cursor Down

2025 - JF AVILES
--]]

-- Generated by JFA TranslateScripts.lua
function getTranslations(langCode)
	return getArrayLanguageStrings()[langCode]
end

-- Generated by JFA TranslateScripts.lua
function getArrayLanguageStrings()
	return {
		["en-us"] = {
			-- {"Comment!", "Comment!"},
		},
	}
end

function getClientInfo()
	return {
		name = SV:T(SCRIPT_TITLE),
		category = "_JFA_Shift_Notes",
		author = "JFAVILES",
		versionNumber = 2,
		minEditorVersion = 65540
	}
end

-- Define a class  "NotesObject"
NotesObject = {
	project = nil,
	timeAxis = nil,
	editor = nil,
	positionGap = -1, -- Pitch or Scroll (up = 1 & down = -1)
	navigation = nil,
	selectedNotes = nil
}

-- Constructor method for the NotesObject class
function NotesObject:new()
    local notesObject = {}
    setmetatable(notesObject, self)
    self.__index = self
	
    self.project = SV:getProject()
    self.timeAxis = self.project:getTimeAxis()
    self.editor =  SV:getMainEditor()
	self.selectedNotes = self.editor:getSelection():getSelectedNotes()
	self.navigation = self.editor:getNavigation()
	
    return self
end

-- Display message box
function NotesObject:show(message)
	SV:showMessageBox(SV:T(SCRIPT_TITLE), message)
end

-- Start processing
function NotesObject:start()	
	
	-- Notes selected
	if #self.selectedNotes > 0 then
		-- For each selected notes
		for _, note in pairs(self.selectedNotes) do
			-- Set new position
			note:setPitch(note:getPitch() + self.positionGap)
		end
	else
		-- No notes selected
        local viewRange = self.navigation:getValueViewRange()
        local newPos = (viewRange[1] + viewRange[2]) / 2
		-- Scroll up/down
        self.navigation:setValueCenter(newPos + self.positionGap)
	end	
end

-- Main process
function main()
	local notesObject = NotesObject:new()

	-- Start process
	notesObject:start()
	
	SV:finish()
end